            opcao3: 'Martin Luther King',
        },
        {
            imagem: require( path + 'menino-ney.jpeg' ),
            opcaoCorreta: 'Menino Ney',
            opcao1: 'Nego Ney',
            opcao2: 'Neymar Junior',
            opcao3: 'Menino Ney',
        },
        {
            imagem: require( path + 'Xi_Jinping.jpg' ),
            opcaoCorreta: 'Xi Jinping',
            opcao1: 'Pyong Lee',
            opcao2: 'Xi Jinping',
            opcao3: 'Ursinho Pooh',
        },
        /*{
