                setScore(score + 1)

            // }
            novaOpcao()
        } else {
            // if (score > 0) {
                navigate('Errou')
                setScore(score - 1)

            // }
            novaOpcao()
        }

    }


    const Separator = () => (
        <View style={styles.separator} />
    );

    return (

        <View style={styles.container}>

            <Text style={{ fontSize: 20, marginBottom: 10, }}>
                Score: {score}
            </Text>

            <Separator />

            {/*<Button style={styles.btn} title={'Nova Opção'} onPress={ novaOpcao }/>*/}


            <Separator />
            <Separator />

            <Image
                style={styles.img}
                source={ imagemVez }
                alt="Logo"
            />

            <Separator />
