            <Separator />

            <Button style={styles.btn} title={opcao2} onPress={ escolha2 }/>

            <Separator />

            <Button style={styles.btn} title={opcao3} onPress={ escolha3 }/>


            {/*<Button title={'Ir Para Tela 1'} onPress={()=> navigate('TelaInicial') }/>*/}

