        alignItems: 'center',
    },
    titulo: {
        fontSize: 40,
        fontWeight: 'bold',
    },
    descricao: {
        fontSize: 17,
        textAlign: 'center',
    },
    pontuacao:{
        fontSize: 30,
    },
    separator: {
        marginVertical: 8,
        borderBottomColor: '#737373',
        borderBottomWidth: StyleSheet.hairlineWidth,
    },
});





export default TelaInicial
